INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (225, 'Na execução de um bola ao chão, um jogador defensor agride seu adversário com o uso de força excessiva, antes de a bola tocar no solo. Qual será a decisão do árbitro?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (226, 'A bola deve ser considerada como um objeto, quando utilizada para agredir o adversário?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (227, 'Um jogador, com a bola em jogo, agride um adversário com uso de força excessiva. Que infração é esta?', 'F', 12), 'false';
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (228, 'Das infrações seguintes, aponte qual delas está descrita nas Regras do Jogo como uma infração punível com advertência, de acordo com a Regra 12.', 'F', 12, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (229, 'Das infrações seguintes, aponte qual delas é uma infração descrita nas Regras do Jogo como uma infração punível, de acordo com a Regra 12.', 'F', 12, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (230, 'Um goleiro segura a bola, após um chute da equipe adversária. No momento em que ele vai lançá-la para fora da área penal, o goleiro,acidentalmente, lança a bola para dentro de seu próprio gol. Este será válido?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (231, 'Dois jogadores da mesma equipe disputam a bola com um adversário...', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (232, 'Qual das seguintes infrações é punida com tiro livre direto?', 'F', 12, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (233, 'Um jogador, com a bola em jogo, agride o árbitro em sua área penal. Qual será a sua decisão?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (234, 'Um jogador será advertido se ele cometer umas das seguintes infrações. Aponte qual das alternativas não será punida com advertência.', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (235, 'Um jogador, com a bola em jogo, agride um companheiro de equipe com uso de força excessiva dentro de sua área penal. Qual será a decisão do árbitro?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (236, 'Um jogador atacante pode tirar a bola que está na palma da mão do goleiro, utilizando a cabeça?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (237, 'No momento em que o goleiro está tentando lançar a bola com as mãos, um adversário a intercepta, impedindo-o de lançá-la. Isto está permitido?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (238, 'Quando o árbitro pode marcar uma falta?', 'F', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (239, 'Um jogador executa um tiro livre dentro de sua área penal. Quando os jogadores adversários podem entrar nesta área?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (240, 'O árbitro marca um tiro livre indireto a favor da equipe atacante fora da área penal. Ele se esquece de levantar o braço e a bola é chutada diretamente no gol adversário. Qual será a decisão do árbitro?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (241, 'Um jogador executa um tiro livre rapidamente. Um adversário, colocado a menos de 9.15 metros da bola, intercepta-a. Qual será a decisão do árbitro?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (242, 'Um jogador executa um tiro livre dentro de sua área penal rapidamente, antes  de que seus adversários tenham saído desta área. Qual será a decisão do árbitro?', 'F', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (243, 'Um jogador executa rapidamente um tiro livre, mesmo tendo um adversário a 5 metros do local da infração. A bola toca no adversário. Qual será a decisão do árbitro?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (244, 'Em um tiro livre direto a favor da equipe defensora dentro da área penal, em que momento a bola entra em jogo?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (245, 'Após a execução de um tiro livre, um jogador volta a tocar a bola após ela bater no árbitro e antes de tocar em outro jogador. Qual será a decisão do árbitro?', 'F', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (246, 'Um jogador executa, rapidamente, um tiro livre indireto em sua área penal. A bola bate em adversário dentro desta área e entra no gol. Este será válido?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (247, 'Um jogador, fora de sua área penal, executa um tiro livre em direção à sua meta. O goleiro tenta evitar que a bola entre no gol, porém mesmo tocando-a, ele não consegue evitá-lo. Qual será a decisão do árbitro?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (248, 'Em um tiro livre, após colocar os jogadores defensores na distância regulamentar, o árbitro observa que o goleiro está arrumando a barreira. Qual será a decisão do árbitro?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (249, 'Na execução de um tiro livre indireto dentro da área penal, o árbitro é obrigado a erguer o braço? Se for obrigatório, quando ele deve baixá-lo?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (250, 'Um tiro livre indireto, executado em direção à meta adversária, bate em um adversário e entra no gol. Este será válido?', 'F', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (251, 'Na execução de um tiro livre, um defensor pede ao árbitro que sua equipe forme a barreira a 9.15 metros da bola. Isto é permitido?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (252, 'Na execução de um tiro livre fora da área penal a favor da equipe defensora, a bola toca no árbitro e, em seguida, entra no gol. Qual será a sua decisão?', 'F', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (253, 'Qual será o local da execução de um tiro livre a favor da equipe defensora dentro de sua área de meta?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (254, 'O árbitro marca um tiro livre direto contra a equipe "A". Um jogador da equipe "B" executa-o e a bola entra diretamente na meta. Este gol será válido?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (255, 'Na execução de um tiro livre, um defensor, que está dentro de sua área penal, passa a bola ao seu goleiro, que também está dentro desta área. Este segura a bola com as mãos. Qual será a decisão do árbitro?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (256, 'Há um tiro livre a favor da equipe defensora dentro da área de meta. Em que local os adversários devem ficar?', 'F', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (257, 'Em um tiro livre em que houver a solicitação de formação de barreira, em que local os adversários devem ficar?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (258, 'Na execução de um tiro livre próximo à linha lateral, o executor chuta a bola diretamente para fora do campo por esta linha. Qual será a decisão do árbitro?', 'F', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (259, 'Qual jogador pode solicitar ao árbitro a fomação da barreira na execução de um tiro livre?', 'F', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (260, 'O árbitro determina a repetição do tiro penal, porque o goleiro se adiantou de sua linha de meta, antes que a bola tenha sido chutada. Outro jogador pode executar o segundo tiro penal?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (261, 'Na execução de um tiro penal, o goleiro se adianta de sua linha de meta, antes que a bola tenha sido chutada e a desvia pela linha de meta para tiro de canto. Qual será a decisão do árbitro?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (262, 'Na execução de um tiro penal, em um campo encharcado, o executor  coloca a bola ao lado da marca penal. Isto é permitido?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (263, 'O árbitro pode marcar um tiro penal, se um defensor cometer uma falta em sua área penal, mesmo que a bola esteja fora desta área?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (264, 'Um jogador, que havia sido advertido com o cartão amarelo, comete uma outra infração punível com cartão amarelo, na execução dos tiros da marca penal para determinar o vencedor da partida. O jogador infrator será expulso por segunda advertência?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (265, 'Em um tiro penal, qual das seguintes afirmações não está correta?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (266, 'O árbitro determina a repetição do tiro penal, porque o goleiro se adiantou em relação à linha de meta, antes da execução. Na repetição deste penal é necessário que o mesmo executor chute a bola?', 'F', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (267, 'O árbitro marca um tiro penal, o executor coloca a bola na marca penal rapidamente e, sem autorização do árbitro, chuta e marca o gol. Qual será a decisão do árbitro?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (268, 'Na execução de um tiro penal, um jogador passa a bola para frente para um companheiro que a chuta e marca o gol. Este será válido?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (269, 'O árbitro dá o sinal de autorização para a execução do tiro penal e, antes de que a bola esteja em jogo, um jogador de cada equipe se coloca a menos de 9.15 m da bola. Qual será a decisão do árbitro?', 'F', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (270, 'Em um tiro penal, o jogador executor passa a bola para trás para um companheiro que chuta e marca o gol. Qual será a decisão do árbitro?', 'F', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (271, 'Um jogador pode trocar de posição com seu goleiro na execução dos tiros da marca penal para determinar o vencedor da partida?', 'F', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (272, 'Um jogador executa o tiro penal antes da autorização do árbitro. O tiro penal será repetido?', 'F', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (273, 'Um gol pode ser marcado diretamente de um arremesso lateral?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (274, 'Qual é a distância que os jogadores adversários devem permanecer na execução de um arremesso lateral?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (275, 'Qual das seguintes afirmações está incorreta em relação à execução de um arremesso lateral?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (276, 'No momento de lançar a bola com as mãos, o executor de um arremesso lateral salta. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (277, 'Quando a bola entra em jogo em um arremesso lateral?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (278, 'Na execução de um arremesso lateral, o adversário pode ficar na frente do executor?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (279, 'Na execução de um arremesso lateral, um jogador adversário pode ficar a menos de 2 m do executor, atrapalhá-lo e, ainda, não ser advertido?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (280, 'Em um arremesso lateral, o jogador executor pisa com um dos pés na linha lateral. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (281, 'A bola ultrapassou completamente a linha lateral. Antes da execução do arremesso, o jogador agride seu adversário com uso de força excessiva. Qual será a decisão do árbitro?', 'F', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (282, 'Um jogador executa um arremesso lateral de joelhos, mas com os seus pés fora da linha lateral do campo. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (283, 'Um arremesso lateral é executado de maneira incorreta. A vantagem pode ser aplicada?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (284, 'Um jogador executa um arremesso lateral incorretamente, mas, na opinião do árbitro, o executor ainda lançou a bola no adversário com uso de força excessiva. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (285, 'Um arremesso lateral é executado em local diferente daquele em que a bola saiu do campo de jogo. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (286, 'Na execução de um tiro de meta, o adversário pode permanecer dentro da área penal?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (287, 'Na execução de um tiro de meta, um adversário intercepta a bola, antes de ela sair da área penal. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (288, 'Quando a bola entrará em jogo em um tiro de meta?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (289, 'Um tiro de meta pode ser executado de qualquer lugar da área de meta?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (290, 'Um jogador executa um tiro de meta, a bola toca no árbitro que está dentro da área penal, mas entra em jogo. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (291, 'Na execução de um tiro de meta, um jogador chuta a bola diretamente para dentro de sua meta. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (292, 'Após a execução de um tiro de meta, mas antes da bola sair da área penal, um adversário entra na área. Neste momento, este jogador é atingido com uma entrada temerária pelo defensor. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (293, 'Na execução de um tiro de meta, antes da bola sair da área penal, ela toca no árbitro, em um defensor e, em seguida, entra na meta da equipe defensora. Qual será a decisão do árbitro?', 'F', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (294, 'Após a execução de um tiro de meta, a bola não sai da área penal. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (295, 'Na execução de um tiro de canto, os adversários deverão permanecer a no mínimo 9.15 metros do(a)...', 'F', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (296, 'Após a execução de um tiro de meta, a bola sai da área penal e, por causa do vento, volta à área e entra no gol, antes de tocar em qualquer outro jogador. Qual será a decisão do árbitro?', 'F', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (297, 'Um gol pode ser marcado diretamente de um tiro de meta?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (298, 'A bola deve ser colocada em qual lugar da área de meta, quando ela sair pelo lado direito das traves?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (299, 'Um jogador executa um tiro de meta, a bola toca no árbitro, que está fora da área penal. Em seguida, a bola é jogada por um adversário que também está fora desta área. Ele domina a bola, chuta-a e marca um gol. O árbitro...', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (300, 'Um jogador executa corretamente um tiro de meta, mas por causa de um forte vento, a bola volta para dentro da área penal. Depois disso, o executor desvia a bola com a mão para evitar que ela entre no gol. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (301, 'Um gol pode ser marcado diretamente de um tiro de meta na meta adversária?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (302, 'Um gol pode ser marcado diretamente de um tiro de canto em ambas as metas?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (303, 'A Regra 16 é...', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (304, 'Um jogador executa um tiro de canto, após a bola tocar no poste de meta, ela entra no gol, enquanto há um jogador atacante em posição de impedimento. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (305, 'É necessário que a bola saia do quarto de círculo para entrar em jogo?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (306, 'Um gol pode ser marcado diretamente de um tiro de canto?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (307, 'Na execução de um tiro de canto, um jogador toca, ligeiramente, a bola para trás com o calcanhar, porém ela não sai do quarto de círculo. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (308, 'Na execução de um tiro de canto, o executor pode colocar a bola sobre a linha do quarto de círculo?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (309, 'Qual a distância mínima que o adversário deve ficar do quarto de círculo, na execução de um tiro de canto?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (310, 'Quando a bola entra em jogo em um tiro de canto?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (311, 'É marcado um tiro de canto a favor de uma das equipes. O goleiro desta equipe decide executá-lo. Qual será a decisão do árbitro?', 'F', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (312, 'Qual das seguintes afirmações está incorreta em relação à execução de um tiro de canto?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (313, 'Na execução de um tiro de canto, o jogador eleva a bola com um ou ambos os pés. Qual será a decisão do árbitro?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (314, 'Na execução de um tiro de canto, a bola é chutada enquanto está se movimentando. O árbitro pode aplicar vantagem?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (315, 'Na execução de um tiro de canto, um jogador executa o tiro e marca um gol em sua própria meta? Qual será a decisão do árbitro?', 'F', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (316, 'O árbitro permitirá que um jogador retire, momentaneamente, a bandeira de canto, na execução de um tiro de canto?', 'F', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (317, 'Na execução de um tiro de canto, a bola, após tocar no árbitro, entra na meta adversária. Qual será a decisão do árbitro?', 'F', 15, 'true');
