INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (577, 'No momento em que a bola está em jogo, um jogador, dentro de sua área penal, lança um objeto em um adversário que está fora desta área com uso de força excessiva. Qual será a decisão do árbitro?', 'D', 12, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (578, 'Enquanto a bola está em jogo, um jogador que está dentro de sua área penal, lança um objeto, uma chuteira, por exemplo, com uso de força excessiva, em uma pessoa que está sentada na área técnica. Qual será a decisão do árbitro?', 'D', 12, 'false');
