INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (580, 'Um jogador sai voluntariamente do campo de jogo para driblar um adversário. No momento em que está passando perto do banco de reservas adversário, ele ofende o médico. Qual será a decisão do árbitro?', 'D', 12, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (581, 'Um jogador comete uma conduta antidesportiva com a bola em jogo. O árbitro paralisa o jogo e adverte o jogador. A partida será reiniciada com…', 'D', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (582, 'No intervalo da partida, o árbitro pode mostrar cartão amarelo ou cartão vermelho a um jogador nos túneis que dão acesso ao vestiário?', 'D', 12, 'true');
