INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (605, 'Um tiro livre indireto foi marcado a favor da equipe adversária, porque o goleiro tocou a bola com as mãos, após havê-la recebido de um passe feito com os pés por um companheiro de equipe. Está infração será punida com cartão amarelo?', 'D', 12, 'true');
