INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (607, 'Um jogador levanta a bola com o pé e a passa com a cabeça em direção ao seu goleiro. Este a domina com os pés. Qual será a decisão do árbitro?', 'D', 12, 'false');
