INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (610, 'O árbitro mostra uma cartão amarelo para um jogador por causa de uma rasteira temerária. Esta advertência é a segunda dele e, por isso, ele é expulso. O assistente mais próximo avisa o árbitro que, antes da rasteira, a bola havia ultrapassado completamente a linha lateral. O árbitro...', 'D', 12, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (611, 'Um jogador, antidesportivamente, levanta a bola com seus pés e faz um passe com a cabeça ao seu goleiro, a fim burlar a Regra. Onde o tiro livre será marcado?', 'D', 12, 'false');
