INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (703, 'Na execução de um tiro livre direto, um jogador chuta a bola em direção ao seu gol. A bola entra. Qual será a decisão do árbitro?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (704, 'Em um tiro livre indireto, a bola bate no árbitro e entra na meta da equipe adversária. Como o jogo será reiniciado?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (705, 'Um jogador executa um tiro livre indireto. A bola é tocada por outro jogador antes de entrar na meta. O árbitro deve validar o gol?', 'D', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (706, 'Um jogador executa rapidamente um tiro livre. Um adversário que está perto da bola impede, deliberadamente, a cobrança rápida. Qual será a decisão do árbitro?', 'D', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (707, 'Um jogador executa um tiro livre direto fora de sua área penal. A bola bate no árbitro e toca novamente neste jogador. Qual será a sua decisão?', 'D', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (708, 'Um tiro livre pode ser executado a de 6 m do local em que a infração foi cometida?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (709, 'O goleiro executa um tiro livre fora de sua área penal. Ele volta a tocar a bola novamente com as mãos voluntariamente, fora de sua área penal. Qual será a decisão do árbitro?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (710, 'Qual é a distância mínima dos adversários na execução de um tiro livre?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (711, 'Um jogador, que não o goleiro, fora de sua área penal, executa um tiro livre fora da área em direção à sua meta. O goleiro toca na bola com a mão para evitar que ela entre no gol, porém ele não consegue evitá-lo. Qual será a decisão do árbitro?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (712, 'Um jogador deseja executar um tiro livre rapidamente. Nesse momento, um adversário está a 4.5 m da bola. Qual será a decisão do árbitro?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (713, 'Em um tiro livre executado rapidamente, um jogador adversário, que não o goleiro, está a 6 m. Este jogador a intercepta a bola com a mão. O árbitro...', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (714, 'Um tiro livre pode ser executado para trás?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (715, 'Um jogador, fora de sua área penal, executa um tiro livre direto em direção à sua meta. A bola entra neste gol, antes da bola tocar em nenhum outro jogador. Qual será a decisão do árbitro?', 'D', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (716, 'Um jogador, que não é o goleiro, executa um tiro livre fora da área penal. O executor toca a bola pela segunda vez com os pés, antes de ela ser tocada por outro jogador. Qual será a decisão do árbitro?', 'D', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (717, 'Quando o árbitro deve baixar o braço na execução de um tiro livre indireto?', 'D', 13, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (718, 'Em um tiro livre indireto a favor da equipe atacante e fora da área penal, o árbitro não levanta o braço para indicar a natureza do tiro livre. A bola é chutada diretamente para o gol por um jogador desta equipe. Qual será a decisão do árbitro?', 'D', 13, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (719, 'Um jogador executa um tiro penal antes da autorização do árbitro. Qual será a sua decisão?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (720, 'Na execução de um tiro penal, o árbitro autoriza a cobrança. Um companheiro do executor adianta-se, antes da bola ser chutada. Se o gol não for marcado, o árbitro…', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (721, 'Um executor de um tiro penal passa a bola para trás para um companheiro que chuta-a para o gol. Qual será a decisão do árbitro?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (722, 'O executor de um tiro penal lança a bola para um companheiro que chuta-a no gol. Isto é permitido?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (723, 'Com a bola em jogo, um defensor, fora de sua área penal, agride um adversário que está dentro desta área penal com uso de força excessiva. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (724, '"Uma partida é estendida em seu tempo para a execução de um tiro penal. O goleiro pode ser substituído antes da execução do tiro penal?"', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (725, '" O executor de um tiro penal faz fintas antes de chutar a bola. Isto é permitido?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (726, 'Os capitães, em comum acordo, resolvem não executar os tiros da marca penal para determinar o vencedor de uma partida, embora isso seja estipulado pelo Regulamento da Competição. Qual será a decisão do árbitro?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (727, 'Após o final da partida, alguns jogadores deixam o campo de jogo e não retornam para a execução dos tiros livres da marca penal para determinar o vencedor de uma partida. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (728, 'Um jogador pode ser advertido ou expulso durante a execução dos tiros da marca penal?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (729, 'Durante a execução dos tiros livres da marca penal, o goleiro é expulso. Eles pode ser substituído por um substituto relacionado?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (730, 'Na execução dos tiros livres da marca penal, o goleiro se lesiona e não pode continuar participando das cobranças. Ele pode ser substituído por um substituto relacionado?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (731, 'Na execução dos tiros livres da marca penal, uma equipe fica com menos de sete jogadores. O árbitro permitirá a continuidade das cobranças?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (732, 'Um jogador lesionado pode não participar da execução dos tiros da marca penal para determinar o vencedor de uma partida?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (733, 'Durante a execução dos tiros penais, um ou mais jogadores se lesionam ou são expulsos. O árbitro deve solicitar a equiparação do número de jogadores por equipe?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (734, 'Na execução dos tiros da marca penal para determinar o vencedor de uma partida eliminatória, o goleiro é expulso. Ele pode ser substituído por um jogador substituto?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (735, 'Na execução dos tiros da marca penal para determinar o vencedor de uma partida, em que local o goleiro da equipe executora deve ficar?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (736, 'Uma partida terminou sem gols. Uma equipe tem 10 jogadores e a outra 8. Se o Regulamento da Competição exigir, os tiros da marca penal serão executados, porém …', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (737, 'Após o árbitro autorizar a execução de um tiro penal, um companheiro do jogador identificado como executor adianta-se e executa o tiro penal. Neste momento, um defensor se coloca a menos de 9.15 m da bola. O árbitro ...', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (738, 'Uma partida é estendida para a execução de um tiro penal. O executor chuta a bola e, após ela se mover uns 10cm, fica parada em uma poça d´água. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (739, 'O que é necessário para que o árbitro puna a equipe executora com um tiro livre indireto, por causa da entrada de um companheiro na área penal, antes da bola entrar em jogo?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (740, 'Na execução de um tiro penal após o final do tempo regulamentar, a bola é chutada, bate na trave e entra na meta. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (741, 'Na execução de um tiro penal, o árbitro deve advertir um jogador que abandona o campo de jogo voluntariamente.', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (742, 'O árbitro autoriza a execução de um tiro penal. Antes que a bola entre em jogo, um companheiro do executor do tiro entra na área penal. O árbitro permitirá que o jogo continue…', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (743, 'Qual jogador pode substituir o goleiro lesionado nos tiros da marca penal para determinar o vencedor de uma partida?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (744, 'Na execução de um tiro penal, jogadores de ambas as equipes entram na área penal antes de que a bola esteja em jogo. Na execução deste tiro penal, o gol é marcado. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (745, 'Na execução dos tiros livres da marca penal para determinar o vencedor de uma partida eliminatória, qual equipe executará a primeira cobrança?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (746, 'No lançamento de um tiro penal, após a bola entrar em jogo, ela é tocada por uma garrafa, antes de ser defendida pelo goleiro ou bater nos postes ou travessão. Qual será a decisão do árbitro?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (747, 'Na execução dos tiros penais para determinar o vencedor de uma partida, quem escolherá a meta em que os tiros serão executados?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (748, 'Os tiros livres da marca penal para determinar o vencedor de uma partida…', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (749, 'Durante a execução dos tiros da marca penal para determinar o vencedor de uma partida eliminatória, uma meta quebra. Os tiros penais devem continuar na outra meta?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (750, 'Um jogador executa um tiro penal para trás. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (751, 'Um tiro penal é repetido. Ele pode ser executado por outro jogador?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (752, 'Após iniciada a execução dos tiros da marca penal, o goleiro, sem estar lesionado, troca de posição com outro jogador. Isto é permitido?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (753, 'No momento em que o árbitro está prestes a autorizar a execução de um tiro penal, um defensor o ofende. O árbitro o expulsa e a sua equipe fica com seis jogadores. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (754, 'Na execução de um tiro penal, um companheiro do executor identificado chuta a bola no gol. O goleiro rebate a bola e ela retorna ao jogador infrator. Este a domina e marca o gol. Qual será a decisão do árbitro?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (755, 'Qual das situações seguintes não é necessária para a marcação de um tiro penal?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (756, 'Um substituição pode ser feita na execução dos tiros da marca penal para determinar o vencedor de uma partida eliminatória?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (757, 'Onde os árbitros assistentes, o árbitro e o quarto árbitro ficam na execução dos tiros da marca penal para determinar o vencedor de uma partida eliminatória?', 'D', 14, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (758, 'Na execução de um tiro penal, a bola estoura antes de entrar na meta. Qual será a decisão do árbitro?', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (759, 'Na execução de um tiro penal, antes da bola entrar em jogo, um companheiro do executor do tiro entra na área penal. Após a execução do tiro penal, o goleiro rebate a bola para tiro de canto. O árbitro...', 'D', 14, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (760, 'A bola está fora de jogo após haver saído pela linha lateral. Antes da execução do arremesso lateral, um jogador dá um chute em um adversário com uso de força excessiva. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (761, 'Após a execução incorreta de um arremesso lateral, a bola vai diretamente a um adversário. O árbitro pode aplicar a vantagem?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (762, 'Um jogador executa um arremesso lateral corretamente, porém a bola não entra no campo. O árbitro...', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (763, 'Um jogador executa um arremesso lateral em direção ao seu goleiro. Este toca a bola com as mãos para evitar que ela entre na meta, porém não consegue. O árbitro...', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (764, 'Um jogador, que executa corretamente um arremesso lateral, lança a bola diretamente em um adversário para voltar a jogar a bola. Ele não o faz de maneira imprudente, temerária ou com uso de força excessiva. O árbitro ...', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (765, 'Um jogador distrai ou atrapalha o executor de um arremesso lateral. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (766, 'O goleiro executa um arremesso lateral. Após a bola entrar em jogo, ele volta a tocá-la pela segunda vez, porém com os pés, antes que a bola tenha tocado em outro jogador. O árbitro…', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (767, 'Um jogador está prestes a executar um arremesso lateral. A bola cai no solo, mas não chega a entrar no campo de jogo. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (768, 'Um jogador executa um arremesso lateral de joelhos. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (769, 'Em um arremesso lateral, existe distância máxima em relação à linha lateral?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (770, 'Um jogador executa um arremesso lateral corretamente. A bola toca no árbitro e sai do campo de jogo, sem tocar em nenhum outro jogador. O árbitro...', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (771, 'Um jogador executa um arremesso lateral com a ponta de seus pés dentro do campo de jogo e com os seus calcanhares sobre a linha lateral. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (772, 'Na execução de um arremesso lateral, um jogador salta em frente ao seu adversário. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (773, 'Na execução de um arremesso lateral, um jogador marca um gol diretamente na meta adversária. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (774, 'Um arremesso lateral foi executado corretamente em direção à meta do jogador executor. Um companheiro dele, que não o goleiro, percebe que a bola está prestes a entrar na meta e evita, voluntariamente, um gol com o uso das mãos. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (775, 'Um jogador retarda a execução de um arremesso lateral. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (776, 'O árbitro assistente sinaliza a saída da bola pela linha lateral. Antes de o árbitro confirmar a sinalização do assistente, um jogador defensor, dentro de sua área penal, agride um jogador atacante com uso de força excessiva. O árbitro...', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (777, 'Em um arremesso lateral executado corretamente, um jogador lança, intencionalmente, a bola em um adversário. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (778, 'Em relação à Regra 15, aponte que afirmação está incorreta. Um arremesso lateral…', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (779, 'Um jogador executa corretamente um arremesso lateral em direção ao seu adversário, porém o faz de maneira temerária. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (780, 'Um gol pode ser marcado diretamente de um arremesso lateral?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (781, 'Um jogador pode executar um arremesso lateral sentado?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (782, 'Para que o procedimento de um arremesso lateral seja correto, qual das afirmações seguintes é necessária?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (783, 'Na execução de um tiro de canto, onde a bola deve ser colocada?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (784, 'Um jogador marca um gol de um arremesso lateral diretamente. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (785, 'Em relação ao arremesso lateral, uma das afirmações seguintes não é verdadeira. Aponte-a.', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (786, 'O jogador executor de um arremesso lateral deve estar de frente para o campo de jogo no momento da execução do arremesso?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (787, 'Em um arremesso lateral, um jogador levanta seus calcanhares no momento da execução. Isto é permitido?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (788, 'Após a execução de um tiro de meta, porém antes que a bola tenha saído da área penal, um adversário entra nesta área e, nesse momento, um defensor o atingi com uma rasteira. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (789, 'Um gol é marcado diretamente de um tiro de meta. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (790, 'Um goleiro executa um tiro de meta e toca a bola pela segunda vez, antes que ela tenha saído da área penal. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (791, 'Na execução de um tiro de meta, a bola bate em um jogador da equipe executora dentro da área penal e, em seguida, entra na meta. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (792, 'Um jogador, que executou corretamente um tiro de meta, toca a bola com a mão de maneira voluntária, quando ela já havia saído da área penal, mas antes de que outro jogador a tenha tocado. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (793, 'Uma equipe que executa um tiro de meta pode ser punida com um tiro penal?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (794, 'Na execução de um tiro de meta, a bola estoura após percorrer 20 m. Qual será a decisão do árbitro?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (795, 'Em um tiro de meta executado pelo goleiro, antes de que a bola toque em outro jogador, ele volta a tocá-la de maneira voluntária com as mãos. Qual será a decisão do árbitro?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (796, 'Na execução de um tiro de meta, o jogador executor chuta a bola para trás em direção à linha de meta. Qual será a decisão do árbitro?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (797, 'Após a execução de um tiro de meta, a bola toca no árbitro antes de sair da área penal. Qual será a decisão do árbitro?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (798, 'Na execução de um tiro de meta, onde os jogadores da equipe adversária devem ficar?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (799, 'Um jogador executa um tiro de meta corretamente. Ele toca a bola com as mãos voluntariamente, após ela ter saído da área, mas antes que ela tenha tocado em outro jogador. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (800, 'Na execução correta de um tiro de meta, a bola estoura após percorrer 20 metros. Qual será a decisão do árbitro?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (801, 'Um jogador adversário pode ficar a menos de 2 metros do executor na cobrança de um tiro de meta?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (802, 'Na execução de um tiro de meta, a bola ultrapassa a linha de meta sem haver saído anteriormente da área penal. Qual será a decisão do árbitro?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (803, 'Após a execução de um tiro de meta, a bola percorre 4m e estoura. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (804, 'Em um tiro de meta, um companheiro do executor toca a bola antes que ela tenha saído da área penal. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (805, 'Na execução de um tiro de meta, após a bola percorrer 10 metros, um atacante a domina e marca um gol. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (806, 'Na execução correta de um tiro de meta, um jogador chuta a bola intencionalmente em um adversário de forma não imprudente, nem temerária e sem o uso de força excessiva, com a intenção de voltar a jogar a bola. O árbitro…', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (807, 'O jogador executor de um tiro de canto toca a bola pela segunda vez antes que outro jogador a tenha tocado. O árbitro...', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (808, 'Na execução de um tiro de canto, a bola bate em um adversário que está a menos de 9.15 m do quarto de círculo e entra na meta. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (809, 'Na execução de um tiro de canto, a bola bate no árbitro e entra na meta. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (810, 'Um jogador executa um tiro de canto. A bola bate no poste de meta e o executor volta a tocar a bola antes que outro jogador a tenha tocado. Qual será a decisão do árbitro?', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (811, 'Na execução correta de um tiro de canto, a bola é tocada por uma pessoa estranha na linha da área de meta, antes de tocar em qualquer outro jogador. O árbitro...', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (812, 'Um tiro de canto será marcado se…', 'D', 15, 'true');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (813, 'Todas as vezes que a bola ultrapassar totalmente a linha de meta, quer seja por terra ou pelo ar, após ter sido tocada por último por um jogador da equipe defensora, o jogo será reiniciado com um tiro de canto?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (814, 'Na execução de um tiro de canto, é necessário que a bola deixe o quarto de círculo para entrar em jogo?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (815, 'Qual das afirmações seguintes está correta em relação ao tiro de canto?', 'D', 15, 'false');
INSERT INTO PERGUNTA (ID, DESCRICAO, LEVEL, REGRA, ALEATORIO) VALUES (816, 'Um jogador executa um tiro de canto em direção à sua meta. A bola toca no goleiro de sua equipe e entra na meta. Qual será a decisão do árbitro?', 'D', 15, 'true');
